import React from "react";
import { View, Text, StyleSheet, LayoutAnimation, TouchableOpacity } from "react-native";
import Firebase from '../firebase';

export default class HomeScreen extends React.Component {
    state = { email: "", name: "" };

    componentDidMount() {
        const { email, name } = Firebase.auth().currentUser;

        this.setState({ email, name });
    }



    render() {
        LayoutAnimation.easeInEaseOut();

        return (
            <View style={styles.container}>
                <Text>Hi {this.state.name}!</Text>
                <TouchableOpacity 
                    style={styles.button} 
                    onPress={() =>this.props.navigation.navigate('Submit')}>
                    <Text style={{ color: "#FFF", fontWeight: "500" }}>Report</Text>
                </TouchableOpacity>

            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "center"
    },
    button: {
        marginHorizontal: 30,
        backgroundColor: "black",
        borderRadius: 4,
        height: 52,
        width: 100,
        alignItems: "center",
        justifyContent: "center"
    }
});
